// frontend/src/pages/Register.js
import React, { useState, useContext } from 'react';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../services/firebase'; // Corrected import path
import { AppContext } from '../App'; // Assuming AppContext is exported from App.js
import { InputField, Button } from '../components/common'; // Assuming common components

const RegisterForm = ({ setCurrentAuthPage }) => {
    const { showMessage, setCurrentPage } = useContext(AppContext);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);

    const validatePassword = (pass) => {
        if (pass.length < 6) {
            return "Password must be at least 6 characters long.";
        }
        return null;
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setLoading(true);

        const passwordError = validatePassword(password);
        if (passwordError) {
            showMessage(passwordError, 'error');
            setLoading(false);
            return;
        }

        if (password !== confirmPassword) {
            showMessage('Passwords do not match!', 'error');
            setLoading(false);
            return;
        }

        try {
            await createUserWithEmailAndPassword(auth, email, password);
            showMessage('Registration successful! You are now logged in.', 'success');
            setCurrentPage('dashboard'); // Redirect to dashboard on successful registration
        } catch (error) {
            console.error('Registration error:', error);
            showMessage(`Registration failed: ${error.message}`, 'error');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-md mx-auto">
            <h2 className="text-3xl font-semibold mb-6 text-center text-blue-600 dark:text-blue-400">Register</h2>
            <form onSubmit={handleRegister} className="space-y-4">
                <InputField
                    label="Email"
                    type="email"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your.email@example.com"
                    required
                />
                <InputField
                    label="Password"
                    type="password"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="********"
                    required
                />
                <InputField
                    label="Confirm Password"
                    type="password"
                    name="confirmPassword"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    placeholder="********"
                    required
                />
                <Button label={loading ? 'Registering...' : 'Register'} type="submit" primary className="w-full" disabled={loading} />
            </form>
            <div className="mt-6 text-center text-gray-600 dark:text-gray-300">
                <p>Already have an account? <a href="#" onClick={() => setCurrentAuthPage('login')} className="text-blue-500 hover:underline">Login here</a></p>
            </div>
        </div>
    );
};

export default RegisterForm;
